import java.util.ArrayList;interface Pet {    void sound();    void food();    int board();}interface Train {    void trick();}abstract class Animal implements Pet {    int age;    String name;    Animal() {    }    int getAge() {        return age;    }    String getName() {        return name;    }    public void sleep() {        System.out.println(name + " goes to sleep.");    }    abstract void special();}class Dog extends Animal implements Train {    Dog(int x, String a) {        age = x;        name = a;    }    public void sound() {        System.out.println(name + " barks loudly!");    }    public void food() {        System.out.println(name + " eats kibble happily.");    }    public int board() {        return 30;    }    public void trick() {        System.out.println(name + " performs a trained trick and rolls over");    }    void special() {        System.out.println(name + " fetches a toy");    }}class Cat extends Animal {    Cat(int x, String a) {        age = x;        name = a;    }    public void sound() {        System.out.println(name + " meows softly");    }    public void food() {        System.out.println(name + " eats kibble with elegance");    }    public int board() {        return 25;    }    void special() {        System.out.println(name + " engages in her special behavior and scratches the post");    }    public void sleep() {        System.out.println(name + " curls up and goes to sleep");    }}class Parrot extends Animal {    Parrot(int x, String a) {        age = x;        name = a;    }    public void sound() {        System.out.println(name + " squawks brightly");    }    public void food() {        System.out.println(name + " pecks at the kibble");    }    public int board() {        return 20;    }    void special() {        System.out.println(name + " performs her special behavior: mimics a phrase");    }    public void sleep() {        System.out.println(name + " tucks her head and goes to sleep");    }}class PetHotelApp {    public static void main(String[] args) {        ArrayList<Pet> pets = new ArrayList<>();        pets.add(new Dog(3, "Buddy"));        pets.add(new Cat(2, "Misty"));        pets.add(new Parrot(5, "Polly"));        double total = 0.0;        for (Pet p : pets) {            Animal a = (Animal) p;            System.out.println(a.getClass().getSimpleName() +                               " (" + a.getName() + ", Age " + a.getAge() + ")");            p.sound();            p.food();            try {                Train t = (Train) p;                t.trick();            } catch (Exception e) { }            a.special();            a.sleep();        }        System.out.println("--- Boarding Cost Summary ---");        for (Pet p : pets) {            Animal a = (Animal) p;            int cost = p.board();            System.out.println(a.getName() + " daily cost: $" + cost);            total += cost;        }        System.out.println("Total Daily Boarding Cost: $" + total);        System.out.println("--- End of Day Report ---");    }}